import { ActionType, createReducer } from 'typesafe-actions';
import { getReposActions } from './github-repos.actions';

export interface IGithubReposState {
    isLoading: boolean;
    data: any;
    error: string | null;
}

const initialState: IGithubReposState = {
    isLoading: false,
    data: null,
    error: null,
};

type GithubReposActions = ActionType<typeof getReposActions>;

export const githubReposReducer = createReducer<IGithubReposState, GithubReposActions>(initialState)
    .handleAction(
        getReposActions.request,
        (state): IGithubReposState => ({
            ...state,
            isLoading: true
        })
    )
    .handleAction(
        getReposActions.success,
        (state, action): IGithubReposState => ({
            ...state,
            isLoading: false,
            data: action.payload
        })
    )
    .handleAction(
        getReposActions.failure,
        (state, action): IGithubReposState => ({
            ...state,
            isLoading: false,
            error: action.payload,
            data: null,
        })
    )
