import { ActionType, createReducer } from 'typesafe-actions';
import { getReposActions, searchReposActions } from './github-repos.actions';

export interface IGithubReposState {
    isLoading: boolean;
    data: any;
    error: string | null;
}

const initialState: IGithubReposState = {
    isLoading: false,
    data: null,
    error: null,
};

type GithubReposActions = ActionType<typeof getReposActions | typeof searchReposActions>;

export const githubReposReducer = createReducer<IGithubReposState, GithubReposActions>(initialState)
    .handleAction(
        getReposActions.request,
        (state): IGithubReposState => ({
            ...state,
            isLoading: true
        })
    )
    .handleAction(
        getReposActions.success,
        (state, action): IGithubReposState => ({
            ...state,
            isLoading: false,
            data: action.payload.map((item: any) => {
                return {
                    title: item.name,
                    lang: item.language,
                    desc: item.description
                }
            })
        })
    )
    .handleAction(
        getReposActions.failure,
        (state, action): IGithubReposState => ({
            ...state,
            isLoading: false,
            error: action.payload,
            data: null,
        })
    )
    .handleAction(
        searchReposActions.request,
        (state): IGithubReposState => ({
            ...state,
            isLoading: true
        })
    )
    .handleAction(
        searchReposActions.success,
        (state, action): IGithubReposState => ({
            ...state,
            isLoading: false,
            data: state.data.filter((item: any) => {
                const a = (
                    item.title.includes(action.payload) ||
                    item.lang.includes(action.payload) ||
                    item.desc.includes(action.payload)
                )
                console.log(a)
                return item;
            })
        })
    )
    .handleAction(
        searchReposActions.failure,
        (state, action): IGithubReposState => ({
            ...state,
            isLoading: false,
            error: action.payload,
            data: null,
        })
    )
