import React from "react";
import './content-card.scss'

interface IProps {
    title: string;
    lang: string;
    desc: string | null;
}

export const ContentCard: React.FC<IProps> = ({title, lang, desc}) => {
    return (
        <div className='card'>
            <h2 className='card-title'>{title}</h2>
            <p className='card-subtitle'>Language: <span>{lang}</span></p>
            <p className='card-subtitle'>Description: <span>{desc}</span>
            </p>
        </div>
    )
}
